{"query":"{
  detached {
    id
    status
    startAt
    tasks {
      name
      vars
    }
    outputs {
      out
      error
    }
  }
}"}

{"query":"mutation {
  detached(
    tasks: ["reminder"] 
    properties: {
      vars: {
        message: "Remind me this"
      }
		}
  ) {
    id
    startAt
    status
    outputs {
      out
      error
    }
  }
}
"}

{"query":"{
  detached(ids: ["65fa05b4ef98d", "3b95f3e48a0c1a"]) {
    id
    status
    startAt
    tasks {
      name
      vars
    }
    outputs {
      out
      error
    }
  }
}"}

{"query":"{
  detached(ids: ["5b3ed8e676f"] status: [success]) {
    id
    status
    startAt
    tasks {
      name
      vars
    }
    outputs {
      out
      error
    }
  }
}"}

{"query":"mutation {
  detached(
    tasks: ["shifter"] 
    properties: {
      vars: {
        message: "Remind me this"
      }
		}
  ) {
    id
    startAt
    status
    outputs {
      out
      error
    }
  }
}
"}

{"query":"mutation {
  detached(
    tasks: ["shifter"] 
    properties: {
      vars: {
        message: "Remind me this"
      }
		}
  	config: {
      start: "2020-06-15T14:10:00-05:00"
    }
  ) {
    id
    startAt
    status
    outputs {
      out
      error
    }
  }
}
"}

{"query":"query {
  tasks {
    title
    tags
    name
    cmds
    env
    vars
    envFile
    description
    dir
    log {
      out
      error
      format
    }
    sources
    deps {
      name
      detached
    }
    ignoreError
  }
}"}

